import React, { Component } from "react";
import { StyleSheet, Text, View, FlatList } from "react-native";
import {SearchBar } from "react-native-elements";


const DATA = [
    {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2020,
        "Year":"2020",
        "Population":326569308,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2019,
        "Year":"2019",
        "Population":324697795,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2018,
        "Year":"2018",
        "Population":322903030,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2017,
        "Year":"2017",
        "Population":321004407,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2016,
        "Year":"2016",
        "Population":318558162,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2015,
        "Year":"2015",
        "Population":316515021,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2014,
        "Year":"2014",
        "Population":314107084,
        "Slug Nation":"united-states"
     },
     {
        "ID Nation":"01000US",
        "Nation":"United States",
        "ID Year":2013,
        "Year":"2013",
        "Population":311536594,
        "Slug Nation":"united-states"
     }
];

const Item = ({ title }) => {
return (
	<View style={styles.item}>
	<Text>{title}</Text>
	</View>
);
};

const renderItem = ({ item }) => <Item title={item.Nation} />;
class Search extends Component {
constructor(props) {
	super(props);
	this.state = {
	loading: false,
	data: DATA,
	error: null,
	searchValue: "",
	};
	this.arrayholder = DATA;
}

searchFunction = (text) => {
	const updatedData = this.arrayholder.filter((item) => {
	const item_data = `${item.Nation.toUpperCase()})`;
	const text_data = text.toUpperCase();
	return item_data.indexOf(text_data) > -1;
	});
	this.setState({ data: updatedData, searchValue: text });
};

render() {
	return (
	<View style={styles.container}>
		<SearchBar
		placeholder="Search Here..."
		lightTheme
		round
		value={this.state.searchValue}
		onChangeText={(text) => this.searchFunction(text)}
		autoCorrect={false}
		/>
		<FlatList
		data={this.state.data}
		renderItem={renderItem}
		keyExtractor={(item) => item.id}
		/>
	</View>
	);
}
}

export default Search;

const styles = StyleSheet.create({
container: {
	marginTop: 30,
	padding: 2,
},
item: {
	backgroundColor: "#f5f520",
	padding: 20,
	marginVertical: 8,
	marginHorizontal: 16,
},
});
